package com.mkpits.datatypes;

public class BooleanDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     boolean isAngry = true;
     boolean ishungry = false;
     boolean isSad = false;
     boolean isMelancholy = true;
     System.out.println("Angry :"+isAngry);
     System.out.println("Hungry :"+ishungry);
     System.out.println("Sad :"+isSad);
     System.out.println("Melancholy  :" +isMelancholy);
	}

}
// boolean value returns either true or false
// boolean is logical data type
// default value is false
// memory space is none